import logo from './logo.svg';
import './App.css';
import Result from './components/Result';
import Button from './components/Button';

import { useState, useRef } from 'react';

function App() {
  let resultRef = useRef(null);
  let inputRef = useRef(null);

  let [result,setRes]=useState(0);
  let [input1,setInput1]=useState(0);
  let [input2,setInput2]=useState(0);

  let [operationName, setOperationName]=useState("+");
  let [equation, setEquation]=useState(0);

  //-----------------------------

  function calculate(e)
  {
    switch(operationName)
    {
      case "*":
        setRes(Number(input1)*Number(input2));
        break;

      case "-":
        setRes(Number(input1)-Number(input2));
        break;
      case "/":
        setRes(Number(input1)/Number(input2));
        break;
        default:
        setRes(Number(input1)+Number(input2));
        break;    
    }
    setInput2(result);
    setInput1(0);
  }

  const buttonsNumber=()=>
  {
    const button = [];
    for(let i = 0; i < 10; i++)
        {
          button.push(<Button text={i} callFunc={()=>receivedNum(i)} styleBtn={styleNum} />);
        }
    return button;
  }
  const buttonsCalc=()=>
  {
    const calc=["+","-","*","/"];

    const button = [];
    for(let i = 0; i < 4; i++)
        {
          button.push(<Button text={calc[i]} callFunc={calculate} styleBtn={styleCalc}/>);
        }
    return button;
  }

  const receivedNum =(data)=>{

    setEquation((prevEquation) => prevEquation*10 + data);

    //setRes((prevRes) => prevRes*10 + data);

  }

  const styleNum={
    flex: "0 0 33.33%"
  }
  const styleCalc={
    flex:"0 0 50%"
  }
  const styleCont={
    flexWrap: "wrap"
  }



  return (
    <div className='flex-container d-flex flex-column align-items-center'>
      <Result result={equation} resultRef={inputRef} />
    <div className='d-flex justify-content-center'>
        <div className='d-flex flex-row-reverse justify-content-center col-5' style={styleCont}>
          {buttonsNumber()}
        </div>
        <div className='d-flex justify-content-center col-3' style={styleCont}>
          {buttonsCalc()}
        </div>
      </div>

      <Button text={operationName} callFunc={calculate} />

      <Result result={result} resultRef={resultRef} />

    </div>

  );
}

export default App;
